puts 'Evenements créés'

Event.all.each do |event|
  User.all.sample(rand(1..6)).each do |user|
    event.users << user
  end
end

puts 'des users participents aux évènements'

User.all.each do |user|
  aid = Article.all[0..(Article.all.size - 3)].sample.id
  ArticleLike.create(user_id: user.id, article_id: aid)
  ArticleLike.create(user_id: user.id, article_id: (aid + 1))
end
puts 'Users ont liké des articles'
